const buyButtons=document.querySelectorAll(".buy"),modalCart=document.querySelector(".modal-added-to-cart"),modalCartCloseButton=modalCart.querySelector(".close-button"),proceedShoppingButton=modalCart.querySelector(".proceed-shopping");modalCartCloseButton.addEventListener("click",function(t){t.preventDefault(),modalCart.classList.remove("modal-show")});for(const t of buyButtons)t.addEventListener("click",function(t){t.preventDefault(),modalCart.classList.add("modal-show")});window.addEventListener("keydown",function(t){27===t.keyCode&&modalCart.classList.contains("modal-show")&&(t.preventDefault(),modalCart.classList.remove("modal-show"))}),proceedShoppingButton.addEventListener("click",function(t){t.preventDefault(),modalCart.classList.remove("modal-show")});